        $(this).parents(".item_wrap_left").siblings(".item_wrap_right").find("img").attr("src",$(this).find("img").attr('big'));
    
    },function(){
        $(this).siblings().removeClass("active");
        $(this).siblings().find("span").hide();
        
    });

